import React, { createContext, useState } from "react";

export const FirstContext = createContext();

export default function MyContexts({ children }) {
  const [st1, setSt1] = useState("web dev");
  const [st2, setSt2] = useState("full stack");

  const sayHello = () => {
    return <h1>Hello from context</h1>;
  };

  return (
    <FirstContext.Provider value={{ st1, setSt1, st2, setSt2, sayHello }}>
      {children}
    </FirstContext.Provider>
  );
}
