import React, { useState } from "react";
import "./Slider.css";
import dataSlider from "./dataSlider";
import BtnSlider from "./BtnSlider";

export default function Slider() {
  const [index, setIndex] = useState(1);

  const nextSlide = () => {
    setIndex((prevIndex) => (prevIndex % dataSlider.length) + 1);
  };

  const prevSlide = () => {
    setIndex(
      (prevIndex) => (prevIndex - 1 + dataSlider.length) % dataSlider.length
    );
  };

  return (
    <div className="container-slider">
      {dataSlider.map((obj, mapIndex) => (
        <div key={obj.id} className={"slide"}>
          <h1>{mapIndex + 1}</h1>
          <img
            src={process.env.PUBLIC_URL + `/imgs/img${mapIndex + 1}.jpg`}
            alt=""
          />
        </div>
      ))}
      <BtnSlider moveSlide={nextSlide} direction="next" />
      <BtnSlider moveSlide={prevSlide} direction="prev" />
    </div>
  );
}
