// TodoList.js
import React, { useState } from "react";
import Task from "./Task";
import { v4 as uuidv4 } from "uuid";
import "bootstrap/dist/css/bootstrap.min.css";
import "./TodoList.css"; // Importez vos styles personnalisés

export default function TodoList() {
  const [tache, setTache] = useState("");
  const [tasks, setTasks] = useState([]);
  const [showTask, setShowTask] = useState(false);

  const handleInputChange = (event) => {
    setTache(event.target.value);
  };

  const handleAddTask = () => {
    if (tache.trim() !== "") {
      setTasks([...tasks, { id: uuidv4(), text: tache }]);
      setTache("");
      setShowTask(true);
    }
  };

  const handleDeleteTask = (id) => {
    setTasks((prevTasks) => prevTasks.filter((task) => task.id !== id));
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-8">
          {" "}
          {/* Ajustez la taille ici */}
          <h1 className="text-center mb-4">Todo-List-App</h1>
          <form className="mb-4">
            <div className="input-group">
              <input
                type="text"
                value={tache}
                onChange={handleInputChange}
                className="form-control"
                placeholder="Entrer une tâche"
              />
              <button
                type="button"
                onClick={handleAddTask}
                className="btn btn-primary"
              >
                Ajouter
              </button>
            </div>
          </form>
          {showTask && (
            <>
              <h2 className="text-center">Liste des choses à faire</h2>
              {tasks.map((task) => (
                <Task
                  key={task.id}
                  txt={task.text}
                  onDelete={() => handleDeleteTask(task.id)}
                />
              ))}
            </>
          )}
        </div>
      </div>
    </div>
  );
}
